// In CryptoList.js
const fetchCryptoList = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/crypto-list');
      setCryptoList(response.data.data);
    } catch (error) {
      console.error('Error fetching cryptocurrency list', error);
    }
  };
  
  // In CryptoDetails.js
  const fetchCryptoDetails = async (id) => {
    setLoading(true);
    try {
      const response = await axios.get(`http://localhost:5000/api/crypto-details/${id}`);
      setCryptoDetails(response.data.data[id]);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching cryptocurrency details', error);
      setLoading(false);
    }
  };
