// CryptoList.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';

const CryptoList = ({ onSelectCrypto }) => {
  const [cryptoList, setCryptoList] = useState([]);
  const [loading, setLoading] = useState(true);

  // Fetch the crypto list from your Express server
  const fetchCryptoList = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/crypto-list');
      setCryptoList(response.data.data);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching cryptocurrency list', error);
    }
  };

  useEffect(() => {
    fetchCryptoList();
  }, []);

  if (loading) {
    return <div>Loading list...</div>;
  }

  return (
    <div>
      <h2>Cryptocurrency List</h2>
      <ul>
        {cryptoList.map((crypto) => (
          <li key={crypto.id} onClick={() => onSelectCrypto(crypto.id)}>
            {crypto.name} ({crypto.symbol})
          </li>
        ))}
      </ul>
    </div>
  );
};

export default CryptoList;
