import React, { useEffect, useState } from 'react';
import axios from 'axios';

const CryptoList = ({ onSelectCrypto }) => {
  const [cryptoList, setCryptoList] = useState([]);
  const [loading, setLoading] = useState(true);
  const [sortConfig, setSortConfig] = useState({ key: 'name', direction: 'ascending' });

  // Fetch the crypto list from the backend API
  const fetchCryptoList = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/crypto-list');
      setCryptoList(response.data.data);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching cryptocurrency list', error);
    }
  };

  useEffect(() => {
    fetchCryptoList();
  }, []);

  // Sorting function
  const sortTable = (key) => {
    let direction = 'ascending';
    if (sortConfig.key === key && sortConfig.direction === 'ascending') {
      direction = 'descending';
    }
    setSortConfig({ key, direction });
  };

  const sortedCryptoList = [...cryptoList].sort((a, b) => {
    if (a[sortConfig.key] < b[sortConfig.key]) {
      return sortConfig.direction === 'ascending' ? -1 : 1;
    }
    if (a[sortConfig.key] > b[sortConfig.key]) {
      return sortConfig.direction === 'ascending' ? 1 : -1;
    }
    return 0;
  });

  if (loading) {
    return <div>Loading list...</div>;
  }

  return (
    <div>
      <h2>Cryptocurrency List</h2>
      <table border="1">
        <thead>
          <tr>
            <th onClick={() => sortTable('name')}>Name</th>
            <th onClick={() => sortTable('symbol')}>Symbol</th>
            <th onClick={() => sortTable('quotes.USD.price')}>Price (USD)</th>
            <th onClick={() => sortTable('quotes.USD.market_cap')}>Market Cap (USD)</th>
            <th onClick={() => sortTable('quotes.USD.percent_change_24h')}>Percent Change (24h)</th>
          </tr>
        </thead>
        <tbody>
          {sortedCryptoList.map((crypto) => (
            <tr key={crypto.id} onClick={() => onSelectCrypto(crypto.id)}>
              <td>{crypto.name}</td>
              <td>{crypto.symbol}</td>
              <td>{crypto.quotes.USD.price.toFixed(2)}</td>
              <td>{crypto.quotes.USD.market_cap.toLocaleString()}</td>
              <td>{crypto.quotes.USD.percent_change_24h}%</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default CryptoList;
